item.params:
USER={$APIUSER} PASSWORD={$APIPASS}
# IP Addr and HTTPS Web management port of the SonicWall. URL={HOST.IP}:{$HOST.PORT2}
# Login using Digest Auth curl -k -i -u $USER:$PASSWORD --digest -X HEAD https://$URL/api/sonicos/auth # If you're using basic auth, use this instead: # curl -k -i -u $USER:$PASSWORD -X POST https://$URL/api/sonicos/auth
# Query the SSL VPN Sessions #curl -k -i -X GET "https://$URL/api/sonicos/reporting/ssl-vpn/sessions" -H "accept: application/json"
# "Logout" by deleting the auth info. curl -k -i -X DELETE "https://$URL/api/sonicos/auth"
